                </View>
                <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center', backgroundColor: "yellow" }}>
                    <Text>hello world 2.........</Text>
                    <Text>hello world 2.........</Text>
                    <Text>hello world 2.........</Text>
                    <Text>hello world 2.........</Text>
                </View>
            </View>
        </SafeAreaView>
    )
}
const styles = StyleSheet.create({
    // logo: {
    //     paddingTop: 20,
    //     fontSize: 30,
    //     textAlign: "center",
    //     justifyContent: "center",
    //     color: primary,
    // },
    container: {
        flex: 1,
        backgroundColor: primary,
    },

})
export default Test
